mapper.select:
SELECT

FROM proxy_selector

    namespace_id = #{namespaceId, jdbcType=VARCHAR}


        AND name LIKE #{nameLike, jdbcType=VARCHAR}


ORDER BY date_updated DESC

mapper.update:
UPDATE proxy_selector
SET
name=#{name, jdbcType=VARCHAR},
plugin_name=#{pluginName,jdbcType=VARCHAR},
type=#{type,jdbcType=VARCHAR},
forward_port=#{forwardPort,jdbcType=INTEGER},
props=#{props,jdbcType=VARCHAR},
date_updated=#{dateUpdated, jdbcType=TIMESTAMP}
WHERE id=#{id, jdbcType=VARCHAR}
AND namespace_id = #{namespaceId, jdbcType=VARCHAR}

mapper.select:
SELECT
FROM proxy_selector WHERE id IN
#{id, jdbcType=VARCHAR}

SELECT

FROM proxy_selector
WHERE id = #{id, jdbcType=VARCHAR}

SELECT ps.id , ps.name, ps.plugin_name , ps.type, ps.forward_port , ps.props, ps.namespace_id
FROM proxy_selector ps inner join discovery_rel dr on ps.id = dr.proxy_selector_id
where dr.discovery_handler_id = #{discoveryHandlerId}
